Bash(/tmp/check_chrome.ps1 << 'PSEOF'
$chromeProcs = Get-CimInstance Win32_Process | Where-Object { $_.Name -eq 'chrome.exe' }
Write-Host "Chrome processes: $\($chromeProcs.Count\)"
if \($chromeProcs.Count -gt 0\) {
    $main = $chromeProcs | Where-Object { $_.CommandLine -notlike '*--type=*' } | Select-Object -First 1
    if \($main\) {
        Write-Host "Main Chrome cmdline:"
        Write-Host $main.CommandLine
        if \($main.CommandLine -like '*--remote-debugging-port*'\) {
            Write-Host "STATUS: Chrome already has remote debugging enabled"
        } else {
            Write-Host "STATUS: Chrome is running WITHOUT remote debugging"
        }
    }
}
PSEOF)

