Bash(/tmp/verify_chrome.ps1 << 'PSEOF'
$mains = Get-CimInstance Win32_Process | Where-Object { $_.Name -eq 'chrome.exe' -and $_.CommandLine -notlike '*--type=*' }
Write-Host "Chrome main processes: $\($mains.Count\)"
foreach \($m in $mains\) {
    $cmd = $m.CommandLine
    if \($cmd.Length -gt 120\) { $cmd = $cmd.Substring\(0, 120\) + "..." }
    if \($m.CommandLine -like '*--remote-debugging-port*'\) {
        Write-Host "  [WinClaw Debug] PID=$\($m.ProcessId\) $cmd"
    } else {
        Write-Host "  [User Chrome]   PID=$\($m.ProcessId\) $cmd"
    }
}
$tcp = Test-NetConnection -ComputerName localhost -Port 9222 -WarningAction SilentlyContinue
Write-Host ""
Write-Host "Port 9222 listening: $\($tcp.TcpTestSucceeded\)"
PSEOF)
